import './App.css';
import Axios from 'axios';
import { useState } from 'react';


function App() {
  const formdata=[{
    name: "",
    rollno:"",
    email:"",
    ratings:"",
  }]
  const url="";
  const [data,setdata] = useState(formdata);

  // function handle(e){
  //   const newdata = {...data}
  //   newdata[e.target.id] = e.target.value
  //   setdata(newdata)
  //   console.log(newdata)
  //   console.log(data)
  // }

  function submit(e){
    e.preventDefault();
    Axios.post(url,{
      name:data.name,
      rollno: parseInt(data.rollno),
      email:data.email,
      ratings:parseInt(data.ratings),
    })
    .then(res=>{
      console.log(res.data)
    })
  }
  const addObjectToArray = obj => {
    setdata(current => [...current, obj]);
  };
  const onSubmit = () => {
    if(formdata[0]){
      console.log(formdata);
    }
    else{
      addObjectToArray();
      console.log(formdata);
    }
  }


  return (
    <>
    <div className="container" >
      <form className="form" onSubmit={onSubmit} >
        <input id="name" value={formdata.name} type="text" className="input" placeholder="Full-Name" required></input>
        <input id="rollno" value={formdata.rollno} type="number" className="input" placeholder="Roll-no." required></input>
        <input id="email" value={formdata.email} type="email" className="input" placeholder="Email" required></input>
        <input id="ratings" value={formdata.ratings} type="number" className="input" min={0} max={10} placeholder="Ratings" required></input>
        <button type="submit" className="submit">SUBMIT</button>
        </form>
    </div>
    </>
  );
}

export default App;
