import './App.css';
import Axios from 'axios';
import { useState } from 'react';


function App() {
  const url="";
  const [data,setdata] = useState({
    name: "",
    rollno:"",
    email:"",
    ratings:"",

  })

  function handle(e){
    const newdata = {...data}
    newdata[e.target.id] = e.target.value
    setdata(newdata)
    console.log(newdata)
  }

  function submit(e){
    e.preventDefault();
  }


  return (
    <>
    <div className="container">
      <form className="form" onSubmit={(e) => submit(e)} >
        <input onChange={(e) => handle(e)} id="name" value={data.name} type="text" className="input" placeholder="Full-Name" required></input>
        <input onChange={(e) => handle(e)} id="rollno" value={data.rollno} type="number" className="input" placeholder="Roll-no." required></input>
        <input onChange={(e) => handle(e)} id="email" value={data.email} type="email" className="input" placeholder="Email" required></input>
        <input onChange={(e) => handle(e)} id="ratings" value={data.ratings} type="number" className="input" min={0} max={10} placeholder="Ratings" required></input>
        <button type="submit" className="submit">SUBMIT</button>
        </form>
    </div>
    </>
  );
}

export default App;
